import React, { Component, Fragment } from "react";
import NavbarAdmin from "../NavbarAdmin";
import ConfirmDeleteCategory from "../ConfirmDeleteCategory";
import AddCategory from "../AddCategoryModal";
import { Icon, Table, Button } from "semantic-ui-react";
import { getDataFromAPI } from "../../../../config/redux/action";
import { connect } from "react-redux";

class Category extends Component {
  state = {
    onConfirmModalShow: false,
    category: ''
  }

  componentDidMount() {
    this.props.getCategory();
  }

  onCloseClick = () => {
    this.setState({
      onModalShow: false,
      onConfirmModalShow: false
    });
  };

  deleteCategory = e => {
    console.log(e);
    const data = {
      category: e.id
    }
    this.setState({
      onConfirmModalShow: 'true',
      category: e.id
    })
  };

  // addCategory = () => {
  //   console.log("Open Add Category Modal ");
  //   this.setState({
  //     onConfirmModalShow: 'true'
  //   })
  // };

  render() {
    const { notes } = this.props;
    return (
      <div>
        <NavbarAdmin />
        <ConfirmDeleteCategory
          onConfirmModalShow={this.state.onConfirmModalShow}
          onCloseClick={() => this.onCloseClick()}
          category = {this.state.category}          
        />
      
        <br />
        <center>
          <Table unstackable style={{ width: "40%" }}>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>Category List</Table.HeaderCell>
                <Table.HeaderCell textAlign="right">Status</Table.HeaderCell>
              </Table.Row>
            </Table.Header>
            <Table.Body>
              {notes.length !== 0 ? (
                <div>
                  {notes.map(bebas => {
                    return (
                      <div>
                        <Table.Row>
                          <Table.Cell>{bebas.id}</Table.Cell>
                          <Table.Cell textAlign="right">
                            <Icon
                              name="trash alternate outline"
                              style={{cursor: 'pointer'}}
                              onClick={() => {
                                this.deleteCategory(bebas);
                              }}
                            ></Icon>
                          </Table.Cell>
                        </Table.Row>
                      </div>
                    );
                  })}
                </div>
              ) : (
                <p>Loading..</p>
              )}
            </Table.Body>
            <Table.Footer>
              <Table.Row>
                <Table.HeaderCell colSpan="3">
                  <Button 
                    floated="right" 
                    primary style={{ fontSize: "16px" }}
                    // onClick = { () => {
                    //   this.addCategory();
                    // }}
                  >
                    Add Category
                  </Button>
                </Table.HeaderCell>
              </Table.Row>
            </Table.Footer>
          </Table>
        </center>
      </div>
    );
  }
}

const reduxState = state => ({
  // why using ntoes? karena di reducer disimpennya di state 'notes' (?)
  notes: state.notes
});

const reduxDispatch = dispatch => ({
  getCategory: data => dispatch(getDataFromAPI(data))
});

export default connect(reduxState, reduxDispatch)(Category);
