import React, { Component, Fragment } from "react";
import NavbarAdmin from "../NavbarAdmin";
import { Icon, Label, Table, Button } from "semantic-ui-react";
import { getDataFromAPI } from "../../../../config/redux/action";
import { connect } from "react-redux";

class Category extends Component {
  componentDidMount() {
    this.props.getCategory();
  }

  deleteCategory = e => {
    console.log(e);
  };

  render() {
    const { notes } = this.props;
    console.log("notes di category", notes);
    return (
      <div>
        <NavbarAdmin />
        <br />
        <center>
        <Table unstackable>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>Category List</Table.HeaderCell>
                <Table.HeaderCell textAlign='right'>Status</Table.HeaderCell>
              </Table.Row>
            </Table.Header>

            <Table.Body>
            {notes.length !== 0 ? (
              <div>
                {notes.map(bebas => {
                  return (
                    <div key={bebas.id}>
                      
                        <Table.Row style={{width:'100%'}}>
                          <Table.Cell>{bebas.id}</Table.Cell>
                          <Table.Cell textAlign='right'>
                            <Icon 
                              name="trash alternate outline"
                              style={{cursor:'pointer'}}
                              onClick={() => {
                                this.deleteCategory(bebas);
                              }}>
                            </Icon>
                          </Table.Cell>
                        </Table.Row>
                      
                    </div>
                  );
                })}
              </div>
            ) : (
              <p>Loading...</p>
            )}
            </Table.Body>

            <Table.Footer>
              <Table.Row>
                <Table.HeaderCell colSpan="3">
                  <Button floated="right" primary style={{ fontSize: "16px" }}>
                    Add Category
                  </Button>
                </Table.HeaderCell>
              </Table.Row>
            </Table.Footer>
          </Table>
        </center>
      </div>
    );
  }
}

const reduxState = state => ({
  // why using ntoes? karena di reducer disimpennya di state 'notes' (?)
  notes: state.notes
});

const reduxDispatch = dispatch => ({
  getCategory: data => dispatch(getDataFromAPI(data))
});

export default connect(reduxState, reduxDispatch)(Category);
