// Header Component
// --------------------------------------------------------

import React, { Component } from 'react';
import classname from 'classnames';
import Fade from 'react-reveal/Fade';
import './styles.scss';

// data dummy
import logoImageFixed from '../../../assets/images/logo/logo-fixed.svg';
import logoImage from '../../../assets/images/logo/logo.svg';

class Header extends Component {

  state = {
    showMobileNavigation: false,
    scrolled: false,
    isActive: '1'
  };

  componentDidMount() {
    this.handleResizeWindow();
    window.addEventListener("resize", this.handleResizeWindow);
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.handleResizeWindow);
  }

  handleWindowScroll = () => {
    const scroll = window.scrollY;
    if (scroll === 0) {
      if (window.innerWidth <= 768) {
        this.setState({ scrolled: true });
      }
      else {
        this.setState({ scrolled: false });
      }
    }
    else {
      if (window.innerWidth <= 768) {
        this.setState({ scrolled: true });
      }
      else {
        if (scroll > 40) {
          this.setState({ scrolled: true });
        } else {
          this.setState({ scrolled: false });
        }
      }
    }
  };

  addMobileNavigation = () => {
    document.body.classList.toggle('remove-scroll');
    this.setState(prevState => ({
      showMobileNavigation: !prevState.showMobileNavigation
    }));
  }

  handleResizeWindow = () => {
    if (window.innerWidth <= 768) {
      this.setState({ scrolled: true });
    }
    else {
      this.setState({ scrolled: false });
      window.addEventListener('scroll', this.handleWindowScroll);
    }
  }

  handleClickNavigation = (e) => {
    var elem = document.getElementsByClassName('content-item');
    elem.classList.remove('active');
  }

  render() {
    const {
      state: { showMobileNavigation, scrolled },
    } = this;

    const classNames = classname('ds-header', {
      'show-navigation': showMobileNavigation,
      'on-scroll': scrolled
    });

    const total = [
      { id: '1', title: 'Home Page', link: '.' },
      { id: '2', title: 'Our Product', link: '/our-product' },
      { id: '3', title: 'About Us', link: '/about-us' }
    ]

    const tabs = total.map(function (el, i) {
      return (
        <li className={this.state.isActive === el.id ? 'content-item active' : 'content-item'} onClick={this.handleClickNavigation}>
          <a href={el.link}>{el.title}</a>
        </li>
      )

    }, this);

    return (
      <Fade>
        <div className={classNames}>
          <div className="container">
            <div className="navbar-logo">
              <a className="logo-image" href=".">
                <img src={logoImage} alt="navbar-logo" />
              </a>
              <a className="logo-image-fixed" href=".">
                <img src={logoImageFixed} alt="navbar-logo-fixed" /></a>
              <div className="hamburger-menu display-mobile-only" onClick={this.addMobileNavigation}><i>icon</i></div>
            </div>
            <div className="navbar-menu">
              <ul className="menu-content">
                {tabs}
              </ul>
            </div>
          </div>

        </div>
      </Fade>
    );
  }
}

export default Header;
