import React, { Component } from "react";
import { Modal, Button } from "semantic-ui-react";
import { connect } from "react-redux";

class AddCategoryModal extends Component {
  state = {
    onConfirmModalShow: false,
    category: ""
  };

  componentDidMount() {}

  onCloseClick = () => {
    this.setState({
      onModalShow: false,
      onConfirmModalShow: false
    });
  };

  render() {
    const { onConfirmModalShow, onCloseClick, category } = this.props;
    console.log("apaan nih ", onConfirmModalShow);
    return (
      <Modal
        style={{ fontSize: "16px" }}
        open={onConfirmModalShow}
        closeOnEscape={onCloseClick}
        closeOnDimmerClick={onCloseClick}
        onClose={onCloseClick}
        size={"small"}
      >
        <Modal.Header>Add Your Category</Modal.Header>
        <Modal.Content>
          <p>Are you sure you want to delete " ?</p>
        </Modal.Content>
        <Modal.Actions style={{ fontSize: "14x" }}>
          <Button onClick={true} negative>
            No
          </Button>
        </Modal.Actions>
      </Modal>
    );
  }
}

const reduxState = state => ({
  // why using ntoes? karena di reducer disimpennya di state 'notes' (?)
  //   notes: state.notes
});

const reduxDispatch = dispatch => ({
  //   getCategory: data => dispatch(getDataFromAPI(data))
});

export default connect(reduxState, reduxDispatch)(AddCategoryModal);
