import React, { Component } from "react";
import { Form, Modal, Button } from "semantic-ui-react";
import { connect } from "react-redux";

class AddCategoryModal extends Component {
  state = {
    category: ""
  };

  componentDidMount() {}

  onCloseClick = () => {
    this.setState({
      onAddCategoryShow: false
    });
  };

  render() {
    const { onAddCategoryShow, onCloseClick } = this.props;
    return (
      <Modal
        style={{ fontSize: "16px" }}
        open={onAddCategoryShow}
        closeOnEscape={onCloseClick}
        closeOnDimmerClick={onCloseClick}
        onClose={onCloseClick}
        size={"small"}
      >
        <Modal.Header>Add Your Category</Modal.Header>
        <Modal.Content>
          <Form>
            <Form.Group widths="equal">
              <Form.Input fluid label="First name" placeholder="First name" />
              <Form.Input fluid label="Last name" placeholder="Last name" />
              <Form.Select
                fluid
                label="Gender"
                options={options}
                placeholder="Gender"
              />
            </Form.Group>
            <Form.Group inline>
              <label>Size</label>
              <Form.Radio
                label="Small"
                value="sm"
                checked={value === "sm"}
                onChange={this.handleChange}
              />
              <Form.Radio
                label="Medium"
                value="md"
                checked={value === "md"}
                onChange={this.handleChange}
              />
              <Form.Radio
                label="Large"
                value="lg"
                checked={value === "lg"}
                onChange={this.handleChange}
              />
            </Form.Group>
            <Form.TextArea
              label="About"
              placeholder="Tell us more about you..."
            />
            <Form.Checkbox label="I agree to the Terms and Conditions" />
            <Form.Button>Submit</Form.Button>
          </Form>
        </Modal.Content>
        <Modal.Actions style={{ fontSize: "14x" }}>
          <Button onClick={onCloseClick} negative>
            Cancel
          </Button>
        </Modal.Actions>
      </Modal>
    );
  }
}

const reduxState = state => ({
  // why using ntoes? karena di reducer disimpennya di state 'notes' (?)
  //   notes: state.notes
});

const reduxDispatch = dispatch => ({
  //   getCategory: data => dispatch(getDataFromAPI(data))
});

export default connect(reduxState, reduxDispatch)(AddCategoryModal);
