import React, { Component } from 'react';
import './styles.scss';
import { Tab } from 'semantic-ui-react';
import { HeroBanner, Header, Footer, ContactUsButton, ImageCardList } from '../../../../components';

class Product extends Component {
  // state = {
  // };

  componentDidMount() {
    window.scrollTo(0, 0)
    if (this.props.location.state !== undefined) {
      console.log("Data Dari Home Lempar Ke Page ini: ", this.props.location.state.productId);
    }
  }

  // render
  render() {

    const panes = [
      {
        menuItem: 'Tab 1',
        render: () => 
        <Tab.Pane attached={false}>
          Tab 1
          <ImageCardList
            history={this.props.history}
            // data={imageJSON}
            />
        </Tab.Pane>,
      },
      {
        menuItem: 'Tab 2',
        render: () => 
        <Tab.Pane attached={false}>
           Tab 2
          <ImageCardList
            history={this.props.history}
            // data={imageJSON}
            />
        </Tab.Pane>,
      },
      {
        menuItem: 'Tab 3',
        render: () => 
        <Tab.Pane attached={false} loading={true}>
           Tab 3
          <ImageCardList
            history={this.props.history}
            // data={imageJSON}
            />
        </Tab.Pane>,
      },
    ]

    // const {
    //   // handleClosePage,
    //   // state: { showSteps }
    // } = this;

    return (
      <div className="p-product">
        <ContactUsButton />
        <Header isActive='our-product' />
        <HeroBanner bannerTitle="Our Product" className='half' />
        <div className='image-card'>
          <div className='container'>
            <ImageCardList
              history={this.props.history}
            // data={imageJSON}
            />
          </div>
        </div>

        <div className="category-section-wrapper">
          <div className="container">
            <Tab 
              menu={{ text: true, fluid: false, vertical: false }} 
              menuPosition='left' 
              panes={panes} 
              onTabChange={this.handleTabChange}
              />
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}


export default Product;
