import React, { Component, Fragment } from 'react';
import { addDataToAPI, getDataFromAPI } from '../../../config/redux/action';
import { connect } from 'react-redux';
import { Card, Button } from 'react-bootstrap';
import axios from 'axios';
import { saveAs } from 'file-saver';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as moment from 'moment';


class Dashboard extends Component {
    state = {
        title: '',
        content: '',
        date: '',
        createdBy: '',
        name: '',
        receiptId: 0,
        price1: 0,
        price2: 0,
    }

    componentDidMount() {
        const userData = JSON.parse(localStorage.getItem('userData'));
        console.log("Didmount userData ", userData)
        // Call getDataAPI from props
        this.props.getNotes(userData.uid)
    }

    handleSaveNote = () => {
        // GET LOCAL STORAGE 
        const userData = JSON.parse(localStorage.getItem('userData'));
        console.log("userData after parse json ", userData);

        const data = {
            title: this.state.title,
            content: this.state.content,
            date: new Date().getTime(),
            userId: userData.uid
        }
        this.props.saveNotes(data);
        console.log("dataafterSUBMIT ", data)
    }

    onInputChange = (e, type) => {
        this.setState({
            [type]: e.target.value
        })
    }

    createAndDownloadPdf = () => {
        console.log("Clicked..!")
        axios.post('/Invoice/create-pdf', this.state)
            .then(() => axios.get('fetch-pdf', { responseType: 'blob' }))
            .then((res) => {
                const pdfBlob = new Blob([res.data], { type: 'application/pdf' });

                saveAs(pdfBlob, 'newPdf.pdf');
            })
    }

    render() {
        const { title, content, date } = this.state;
        const { notes } = this.props;
        console.log(moment().format('LLLL'));
        console.log("Hasil notes ", notes)
        return (
            <div className="container">
                <nav className="navbar navbar-expand-lg navbar-light bg-light">
                    <a class="navbar-brand" href="#">CHRISTIAN IRWANDI</a>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>

                    <div className="collapse navbar-collapse" id="navbarSupportedContent" >
                        <ul className="navbar-nav mr-auto" >
                            <li className="nav-item active">
                                <a className="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Link</a>
                            </li>
                            <li className="nav-item dropdown">
                                <a className="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                    Dropdown</a>
                                <div className="dropdown-menu" aria-labelledby="navbarDropdown">
                                    <a className="dropdown-item" href="#">Action</a>
                                    <a className="dropdown-item" href="#">Another action</a>
                                    <div className="dropdown-divider"></div>
                                    <a className="dropdown-item" href="#">Something else here</a>
                                </div>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link disabled" href="#" tabindex="-1" aria-disabled="true">Disabled</a>
                            </li>
                        </ul>
                        <form className="form-inline my-2 my-lg-0">
                            <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
                            <button className="btn btn-outline-success my-2 my-sm-0" >Search</button>
                        </form>
                    </div>
                </nav>

                <br />

                <form style={{ marginLeft: "25%", marginRight: "25%" }}>
                    <div class="form-group">
                        <label for="exampleInputEmail1">Title</label>
                        <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" value={title} onChange={(e) => this.onInputChange(e, 'title')} />
                        <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                    </div>
                    <div class="form-group">
                        <label for="exampleInputPassword1">Content</label>
                        <input type="text" class="form-control" id="exampleInputPassword1" value={content} onChange={(e) => this.onInputChange(e, 'content')} />
                    </div>
                    <button class="btn btn-primary" onClick={this.handleSaveNote}>Submit</button>
                </form>
                <br />
                <hr />
                <br />
                {
                    notes.length > 0 ? (
                        <Fragment>
                            {
                                notes.map(bebas => {
                                    return (
                                        <div key={bebas.id}>
                                            <Card >
                                                <Card.Header as="h5">{bebas.data.title}</Card.Header>
                                                <Card.Body>
                                                    <Card.Title>{bebas.data.content}</Card.Title>
                                                    <Card.Text>{moment(bebas.data.date).format('LLLL')}</Card.Text>
                                                </Card.Body>
                                            </Card>
                                            <br />
                                        </div>
                                    )
                                })
                            }
                        </Fragment>
                    ) : null
                }
            </div>
        )
    }
}

const reduxState = (state) => ({
    userData: state.user,
    notes: state.notes
    // Dari actionnya yang berhubung ke reducer
})

const reduxDispatch = (dispatch) => ({
    saveNotes: (data) => dispatch(addDataToAPI(data)),
    getNotes: (data) => dispatch(getDataFromAPI(data))
})

export default connect(reduxState, reduxDispatch)(Dashboard);
