import React, { Component, Fragment } from "react";
import NavbarAdmin from "../NavbarAdmin";
import { Icon, Label,  Table, Button } from "semantic-ui-react";
import {
  getDataFromAPI
} from "../../../../config/redux/action";
import { connect } from "react-redux";

class Category extends Component {
  state = {
    category: ''
  }

  componentDidMount(){
    this.props.getCategory();
  }

  render() {
    const { notes } = this.props;
    console.log("notes di category", notes)
    return (
      <div>
        <NavbarAdmin />
        <p>asdfasdfasdf</p>
        <center>
        <Table celled style={{width: '40%'}}>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Category List</Table.HeaderCell>
              <Table.HeaderCell>Status</Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>
            <Table.Row>
              <Table.Cell>
                adsf
              </Table.Cell>
              <Table.Cell>
              <Icon
              name="trash alternate outline"
            ></Icon>
              </Table.Cell>
            </Table.Row>
          </Table.Body>

          {notes.length !== 0 ? (
            <Fragment>
              {notes.map(bebas => {
                return (
                  <div className="gridContainer" key={bebas.id} style={{display: 'inline-block', width: '25%', 'text-align': '-webkit-center'}}>
                    <div>
                      <p
                        style={{
                          cursor: "pointer",
                          width: "-webkit-max-content" /* Chrome */
                        }}
                        onClick={() => {
                          this.getDetail(bebas);
                        }}
                      >
                        {bebas.id}
                      </p>
                    </div>
                  </div>
                );
              })}
            </Fragment>
          ) : (
            <p>Loading...</p>
          )}

          <Table.Footer>
            <Table.Row>
              <Table.HeaderCell colSpan="3">
                  <Button floated="right" primary style={{fontSize: '16px'}}>Add Category</Button>
              </Table.HeaderCell>
            </Table.Row>
          </Table.Footer>
        </Table>
        </center>
        
      </div>
    );
  }
}

const reduxState = state => ({
  // why using ntoes? karena di reducer disimpennya di state 'notes' (?)
  notes: state.notes
});

const reduxDispatch = dispatch => ({
  getCategory: data => dispatch(getDataFromAPI(data))
})

export default connect (reduxState, reduxDispatch)(Category);
