// Header Component
// --------------------------------------------------------

import React, { Fragment, Component } from 'react';
import PropTypes from 'prop-types';
import classname from 'classnames';
import './styles.scss';

class Header extends Component {
  _isMounted = false;

  state = {
    showMobileNavigation: false,
    scrolled: false
  };

  componentDidMount() {
    this._isMounted = true;
    window.addEventListener('scroll', this.handleWindowScroll);
    removeScrollOnPopup(false);
  }

  componentWillUnmount() {
    this._isMounted = false;
    window.removeEventListener('scroll', this.handleWindowScroll);
  }

  handleWindowScroll = () => {
    const scroll = window.scrollY;
    if (scroll > 24) {
      this.setState({ scrolled: true });
    } else {
      this.setState({ scrolled: false });
    }
  };

  handleLoginButton = () => {
    const { onShowLoginMobile } = this.props;
    onShowLoginMobile(true);
  };

  handleProductsButton = () => {
    const { onShowProductsMobile } = this.props;
    onShowProductsMobile(true);
  };

  handleMobileNavigation = () => {
    const { showMobileNavigation } = this.state;
    this.setState({ showMobileNavigation: !showMobileNavigation }, () => {
      const { showMobileNavigation } = this.state;
      if (showMobileNavigation) {
        removeScrollOnPopup(true);
      } else {
        removeScrollOnPopup(false);
      }
    });
  };

  handleClickSelectLanguageMobile = () => {
    const { showLanguageMobile } = this.state;
    this.setState({ showLanguageMobile: !showLanguageMobile });
  };

  render() {
    const {
      handleMobileNavigation,
      handleClickSelectLanguageMobile,
      handleLoginButton,
      handleProductsButton,
      state: { showMobileNavigation, showLanguageMobile, scrolled },
    } = this;

    const classNames = classname('ds-header', {
      'show-mobile-menu': showMobileNavigation,
      'show-language': showLanguageMobile,
      'window-scrolled': scrolled || productOnScroll
    });

    return (
      <div className={classNames}>
        <div class="container">
          <div class="navbar-logo"><a class="logo-image" href="."><img src="/assets/images/logo/logo.svg" alt="navbar-logo" /></a><a class="logo-image-fixed" href="."> <img src="/assets/images/logo/logo-fixed.svg" alt="navbar-logo-fixed" /></a>
            <div class="hamburger-menu display-mobile-only"><i>icon</i></div>
          </div>
          <div class="navbar-menu">
            <ul class="menu-content">
              <li class="content-item active"><a class="active" href="/Home">Home</a></li>
              <li class="content-item"><a href="/Product">Our Product</a></li>
              <li class="content-item"><a href="/About-Us">About Us</a></li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

Header.propTypes = {
  isLogin: PropTypes.bool,
  isClassicDashboard: PropTypes.bool,
  activeMenu: PropTypes.string,
  avatar: PropTypes.string,
  onShowLoginMobile: PropTypes.func,
  onShowProductsMobile: PropTypes.func,
  productOnScroll: PropTypes.bool
};

Header.defaultProps = {
  isLogin: false,
  isClassicDashboard: false,
  activeMenu: '',
  avatar: '',
  onShowLoginMobile: () => { },
  onShowProductsMobile: () => { },
  productOnScroll: false
};

export default Header;
