import React, { Component, Fragment } from 'react';
import './styles.scss';
import {
  getDataFromAPI,
  getDetailProductImages
} from "../../../../config/redux/action";
import { Tab } from 'semantic-ui-react';
import { connect } from "react-redux";
import { HeroBanner, Header, Footer, ContactUsButton, ImageCardList } from '../../../../components';

class Product extends Component {
  state = {
    panes: [
      {
        menuItem: '',
        data: {}
      }
    ],
    clickedImage: []
  };

  componentDidMount() {
    window.scrollTo(0, 0)
    if (this.props.location.state !== undefined) {
      console.log("Data Dari Home Lempar Ke Page ini: ", this.props.location.state.productId);
    }

    //get Data From Firebase
    const user = "user";
    this.props.getNotes(user);

    this.handleTitleTab(this.props.getNotes(user));
  }

  handleTitleTab = (data) => {
    data.then(data => {
      // you can access your data here
      console.log("HANDLE PROPS: ", this.props.notes)

      this.props.notes.map(data => {
        this.state.panes.push({
          menuItem: data.id,
          data: data
        })
      })

    })
  }

  handleTabChange = (e) => {
    console.log(e.target.innerText.replace(' ', ''));

    const myItems = this.state.panes

    const newArray = myItems.filter(target => target.menuItem === e.target.innerText.replace(' ', ''))

    this.setState({
      clickedImage: newArray
    })

    console.log("INI DATA GUE: ", newArray[0]);
  };

  // getDetailImages = e => {
  //   const data = {
  //     category: e.id
  //   };
  //   //props redux
  //   this.props.showDetailProductImages(data);
  //   console.log("E pas klik detail ", e);
  // };

  // render
  render() {
    const {
      handleTabChange,
      getDetailImages,
      props: {
        notes,
        moreImage,
      },
      state: { panes, clickedImage }
    } = this;

    console.log("INI PANES: ",panes)
    console.log("INI THIS PANES: ",this.state.panes)
    // {
    //   notes.map(data => {
    //     console.log("INI DATA: ", data.id);
    //     panes.push({
    //       menuItem: data.id,
    //       data: data
    //     })
    //   })
    // }

    // let array = panes.filter(target => target.menuItem === 'Spring')

    // const myItems = panes

    // const newArray = myItems.filter(target => target.menuItem === 'Spring')

    // this.setState({
    //   clickedImage: newArray
    // })

    // console.log("INI DATA GUE: ", clickedImage);

    return (
      <div className="p-product">
        <ContactUsButton />
        <Header isActive='our-product' />
        <HeroBanner bannerTitle="Our Product" className='half' />

        <div className="category-section-wrapper">
          <div className="container">
            <Tab
              menu={{ text: true, fluid: false, vertical: false }}
              menuPosition='left'
              // defaultActiveIndex={1}
              panes={panes}
              onTabChange={handleTabChange}
            />
          </div>
        </div>

        <div className='image-card'>
          <div className='container'>
            <ImageCardList
              data={moreImage}
              history={this.props.history}
            />
          </div>
        </div>

        <Footer />
      </div>
    );
  }
}

const reduxState = state => ({
  notes: state.notes,
  moreImage: state.moreImage
});

const reduxDispatch = dispatch => ({
  getNotes: data => dispatch(getDataFromAPI(data)),
  showDetailProductImages: data => dispatch(getDetailProductImages(data))
});

export default connect(reduxState, reduxDispatch)(Product);
