import React from 'react';
import PropTypes from 'prop-types';

// plugins
import Fade from 'react-reveal/Fade';


// components
// eslint-disable-next-line import/no-cycle
import {
  TextBodyCopy,
  ContentTiles
} from '../../../components';

// styles
import './styles.scss';


const ProductCard = ({ backgroundImage, title, onClick }) => {
  const backgroundImageStyle = {
    backgroundImage: `url(${backgroundImage})`
  };

  return (
    <div className='m-product-card'>
      <Fade>
        <ContentTiles className="hover-able">
          <a onClick={onClick} className='link-placeholder'>
            {title}
          </a>
          <div className='content' style={backgroundImageStyle}>
            <TextBodyCopy weight='bold' color='black'>
              {title}
            </TextBodyCopy>
          </div>
        </ContentTiles>
      </Fade>
    </div>
  );
};

ProductCard.propTypes = {
  backgroundImage: PropTypes.string,
  onClick: PropTypes.func,
  title: PropTypes.string
};

ProductCard.defaultProps = {
  backgroundImage: '',
  onClick: () => { },
  title: ''
};

export default ProductCard;
